package d0321;

public class PPExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A  = 5;
		
		System.out.println(A++); //9번 줄에선 A를 먼저 출력하고, 뒤에 나오는 출력값에 +1이 됨.
		System.out.println(A);
		
		int B = 5;
		
		System.out.println(++B); //
		System.out.println(B);
		
	
	}

}
